mapper.select:
SELECT *
  FROM opeaceful.attendance
 WHERE USER_NO = #{userNo}
   AND DATE(WORK_DATE) = DATE(NOW());

mapper.update:
UPDATE attendance
   SET WORK_OFF = DATE_FORMAT(now(),'%H:%i')
 WHERE USER_NO = #{userNo}
   AND WORK_DATE IS NOT NULL
   AND WORK_ON IS NOT NULL
   AND DATE(WORK_DATE) = DATE(NOW());
