mapper.select:
SELECT *
FROM BOARD_TYPE
ORDER BY BOARD_NAME;

SELECT BOARD_NO, BOARD_TITLE, BOARD_CONTENT, BOARD_WRITER, CREATE_DATE, COUNT ,FIXED FROM BOARD B
SELECT BOARD_NO, BOARD_TITLE, BOARD_CONTENT, BOARD_WRITER, M.USER_NAME, P.P_NAME, CREATE_DATE, COUNT, SECRET FROM BOARD B LEFT JOIN MEMBER M ON B.BOARD_WRITER = M.USER_NO LEFT JOIN USER_DEPARTMENT D ON D.USER_NO = M.USER_NO LEFT JOIN POSITION P ON D.P_CODE = P.P_CODE
WHERE BOARD_NO = #{boardNo}

mapper.update:
UPDATE BOARD SET
COUNT = COUNT +1
WHERE BOARD_NO = #{boardNo}

mapper.insert:
INSERT INTO BOARD_FILE SELECT F.* FROM (
SELECT #{file.fileNo} AS FILE_NO, #{file.boardNo} AS BOARD_NO, #{file.originFile} AS ORIGIN_FILE, #{file.changeFile} AS CHANGE_FILE FROM DUAL
) F

mapper.delete:
DELETE FROM BOARD_FILE
WHERE FILE_NO IN ( 
						#{file.fileNo}
					 )
